import React from 'react';
import Picker from './Picker';
import './App.css';

/**
 * Simple component with no state.
 *
 * See the basic-react from lecture for an example of adding and
 * reacting to changes in state.
 */
class App extends React.Component {
  /**
   *
   * @param {props} props
   */
  constructor(props) {
    super(props);
    this.state = {date: '', disabled: true};
  }
  /**
   *
   * @param {event} event event
   */
  handleInput = (event) => {
    this.setState({date: event.target.value});
    const date = new Date(this.state.date);
    const put = this.state.date.split( '/' );
    if (date.getMonth() + 1 === +put[0] &&
      date.getDate() === +put[1] &&
      date.getFullYear() === +put[2]) {
      console.log('ok');
      }
  };

  /**
   * @return {object} a <div> containing an <h2>
   */
  render() {
    return (
      <div>
        <h2>CSE186 Assignment 4 - React I</h2>
        <Picker />
        <input
          id="date"
          type="text"
          placeholder='MM/DD/YYYY'
          onInput={ (event) => this.handleInput(event)}
        />
        <button
          id='set'
          disabled={this.state.disabled}
        >Set</button>
      </div>
    );
  }
}

export default App;
