 *
 * You may not use, distribute, publish, or modify this code without
 * the express written permission of the copyright holder.
 */

import React from 'react';
import loader from './data/loader';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
// import Button from '@mui/material/Button';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
// import Divider from '@mui/material/Divider';
// import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import emails from './data/emails.json';
// import Divider from '@mui/material/Divider';


loader(); // do not remove this!

/**
 * Simple component with no state.
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @return {object} JSX
 */
function App() {
  const drawerWidth = 240;
  const navItems = ['Inbox', 'Trash'];
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [inboxOpen, setInboxOpen] = React.useState('Inbox');

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  /** 
  const hadleInboxOpen = () => {
    setInboxOpen('Inbox');
  };
  */

  const drawer = (
    <Box onClick={handleDrawerToggle} sx={{textAlign: 'center'}}>
      <List>
        {navItems.map((item) => (
          <ListItem key={item} disablePadding>
            <ListItemButton sx={{textAlign: 'center'}}>
              <ListItemText primary={item} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <Box sx={{display: 'flex'}}>
      <Box component="toggle"
        sx={{border: 1, marginTop: 7,
          display: {xs: 'none', sm: 'block'}}}>
        <List>
          {navItems.map((item) => (
            <ListItem key={item} disablePadding>
              <ListItemButton sx={{textAlign: 'center'}}>
                <ListItemText primary={item} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Box>
      <AppBar component="nav">
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{mr: 2, display: {sm: 'none'}}}
          >
            <MenuIcon />
            <Typography
              variant="h6"
              component="div"
              paddingLeft="10px"
              sx={{mr: 2, display: {sm: 'none'}}}
            >
            CSE186 -
            </Typography>
          </IconButton>
          <Typography
            variant="h6"
            component="div"
            sx={{flexGrow: 1, display: {xs: 'none', sm: 'block'}}}
          >
            CSE186 -
          </Typography>
        </Toolbar>
      </AppBar>
      <Box component="nav">
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
          sx={{
            'display': {xs: 'block', sm: 'none'},
            '& .MuiDrawer-paper': {boxSizing: 'border-box', width: drawerWidth},
            'marginTop': 7,
          }}
        >
          {drawer}
        </Drawer>
      </Box>
      <Box component="main" sx={{p: 3}}>
        <Grid container spacing={5} >
          <Grid item xs={50}>
          </Grid>
          <Grid item xs={50} sm={30}>
            {emails.filter((email) => (email.mailbox.
              includes(inboxOpen).map( (email) =>
              <tr key={email.id}>
                <td>{email.from.name}</td>
                <td>{email.subject}</td>
                <td>{email.received}</td>
                <td>{email.mailbox}</td>
              </tr>
            )))}
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
}

export default App;
