 * the express written permission of the copyright holder.
 */

/**
 * Web resources:
 * https://codesandbox.io/s/mm5dth?file=/demo.tsx:868-1084
 */

import React from 'react';
import loader from './data/loader';
import emails from './data/emails.json';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';


loader(); // do not remove this!

const navItems = ['Home', 'About', 'Contact'];

const drawer = (
  <Box onClick={handleDrawerToggle} sx={{ textAlign: 'center' }}>
    <Typography variant="h6" sx={{ my: 2 }}>
      MUI
    </Typography>
    <Divider />
    <List>
      {navItems.map((item) => (
        <ListItem key={item} disablePadding>
          <ListItemButton sx={{ textAlign: 'center' }}>
            <ListItemText primary={item} />
          </ListItemButton>
        </ListItem>
      ))}
    </List>
  </Box>
);

/**
 * Simple component with no state.
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @return {object} JSX
 */
function App() {
  return (
    <Grid container spacing={1} >
      <Grid item xs={12}>
        <Paper className='Paper'>
        Let&apos;s make this look way better with Material-UI, eh?</Paper>
      </Grid>
      <Grid item sm={2} sx={{display: {xs: 'none', sm: 'inline'}}}>
        <Paper>Nav Bar</Paper>
        <Paper>Nav #1</Paper>
        <Paper>Nav #2</Paper>
        <Paper>Nav #3</Paper>
        <Paper>Nav #4</Paper>
        <Paper>Nav #5</Paper>
      </Grid>
      <Grid item xs={12} sm={10}>
        <Paper className='Paper'>Content</Paper>
        <Paper>
          {emails.map((email) => (
            <tr key={email.id}>
              <td>{email.from.name}</td>
              <td>{email.subject}</td>
              <td>{email.received}</td>
              <td>{email.mailbox}</td>
            </tr>
          ))}
        </Paper>
      </Grid>
    </Grid>
  );
}

export default App;
